                            this.props.fields.map( ( y, k ) => {
                                return ( 
                                    <td
                                        key={ k }
                                        className="m-auto py-2"
                                    >
                                        { x[ y ] }
                                    </td>
                                )
                            } ) 
                        }
                        { this.appendHandler( x ) }
                    </li>
                );
            } )
        } else {
            return "Sem registros."
        }
    }
    finderHandler ( value ) {
        if ( value ) {
            fetch( this.props.withFinder + "/" + this.props.scope + "/" + value )
            .then( r => r.json() )
            .then( j => {
                if ( j ) {
                    this.setState( { data: j.map( x => unPrefixObject( x ) ) } );
                } else {
                    this.initialize();
                }
            } )
        } else {
            this.initialize();
        }
    }
    filterHandler () {
        if ( this.props.filter ) {
            return (
                <label>
                    <small>
                        { this.props.filterLabel }
                    </small>
                    <input
                        type="text"
                        className="form-control"
                        onChange={ ev => this.finderHandler( ev.target.value ) }
                    />
                </label>
            )
        }
        return ""
    }
    render () {
        return (
            <div>
                { this.filterHandler() }
                <ul
                    className="list-group"
                >
                    { this.preppendHander() }
                    { this.contentHandler() }
                </ul>
            </div>
        );
    }
