export class SeeWithoutPrefix extends Component {
    state = {
        data: []
    }
    initialize () {
        fetch( this.props.url )
        .then( r => r.json() )
        .then( j => this.setState( { data: ( j ? j.map( x => unPrefixObject( x ) ) : [] ) } ) );
    }
    componentDidMount () {
        this.initialize();
    }
    preppendHander () {
        if ( this.props.preppend ) {
            return (
                <li className="list-group-item d-flex">
                    {
                        this.props.preppend.map( ( y, k ) => {
                            return ( 
                                <div
                                    key={ k }
                                    className="m-auto py-1"
                                >
                                    { y }
                                </div>
                            )
                        } )
                    }
                </li>
            )
        }
    }
    appendHandler ( x ) {
        if ( this.props.append ) {
            if ( x ) {
                return (
                    <div className="ml-auto my-auto">
                        { this.props.append( x ) }
                    </div>
                );
            }
        }
    }
    finderHandler ( value ) {
        if ( value ) {
            fetch( this.props.withFinder + "/" + this.props.scope + "/" + value )
            .then( r => r.json() )
            .then( j => {
                if ( j ) {
                    this.setState( { data: j.map( x => unPrefixObject( x ) ) } );
                } else {
                    this.initialize();
                }
            } )
        } else {
            this.initialize();
        }
    }
    contentHandler () {
        if ( this.state.data.length > 0 ) {
            return this.state.data.map( ( x, i ) => {
                return(
                    <li
                        key={ i }
                        className="list-group-item d-flex"
                    >
                        {
                            this.props.fields.map( ( y, k ) => {
                                return ( 
                                    <td
                                        key={ k }
                                        className="m-auto py-2"
                                    >
                                        { x[ y ] }
                                    </td>
                                )
                            } ) 
                        }
                        { this.appendHandler( x ) }
                    </li>
                );
            } )
        } else {
            return "Sem registros."
        }
    }
    render () {
        return (
            <div>
                <label>
                    <small>
                        Busca por nome
                    </small>
                    <input
                        type="text"
                        className="form-control"
                        onChange={ ev => this.finderHandler( ev.target.value ) }
                    />
                </label>
                <ul
                    className="list-group"
                >
                    { this.preppendHander() }
                    { this.contentHandler() }
                </ul>
            </div>
        );
    }
}

