import React, {
    Component
} from "react";
import Select from 'react-select';

import {
    peopleUrl,
    productsUrl,
    getInit,
    paymentsUrl,
    scopeName,
} from '../../settings';
import {
    responseWindow,
    unPrefixObject
} from "../../functions";

import Http from '../http';


export class Products extends Component {
    state = {
        options: [],
        items: []
    };

    componentDidMount () {
        fetch( productsUrl + "?product_scope=product" )
        .then( r => r.json() )
        .then( j => {
            if ( j ) {
                const options = j.map( x => {
                    let y = unPrefixObject( x );
                    y.amount = 0;
                    return y;
                } );
                this.setState( { options: options } );
            }
        } );
    }
    itemsHandler = v => {
        if ( v ) {
            const items = v.map( x => {
                if ( ! this.state.items.some( item => item.value === x.value ) ) {
                    x.amount = 1;
                }
                return x;
            } );
            this.setState( { items: items } );
        }
    };

    changeHandler ( item, amount ) {
        const options = this.state.options.map( i => {
            if ( i.id === item.id ) {
                let nItem = item;
                nItem.amount = amount
                return nItem;
            }
            return i;
        } );
        this.setState(
            { options: options },
            () => {
                if ( this.props.onChange ) {
                    this.props.onChange( this.state.options.filter( op => op.amount > 0 ) )
                }
            }
        );
    }

    render() {
        return (
            <div>
                { this.state.options.map( ( option, i ) => {
                    return (
                        <div key={ i } >
                            <span>
                                <strong>{ option.name }</strong>
                                <span className="text-success px-2">{ option.price }</span>
                            </span>

                            <div className="ml-auto">
                                <input
                                    type="number"
                                    min="0"
                                    className="form-control"
                                    value={ option.amount }
                                    onChange={ ev => this.changeHandler( option, ev.target.value ) }
                                />
                            </div>
                        </div>
                    );
                } ) }
            </div>
        );
    }
}


